mapper.select:
select coi.id, coi.code, coi.title, coi.content, coi.name, coi.type_id, coi.brand, coi.model, coi.quality, coi.user_id, coi.click_num, coi.click_time, coi.price, coi.store_num, coi.create_time, coi.update_time, coi.status, coi.images, ui.code, ui.name as userName, ui.phone, ct.name as typeName from commodity_info coi left join owner_info ui on (ui.id = coi.user_id) left join commodity_type ct on (ct.id = coi.type_id) where 1 = 1
AND coi.id = #{commodityInfo.id}
AND ui.user_id = #{commodityInfo.userId}
AND ui.name LIKE CONCAT('%',#{commodityInfo.userName},'%')
AND ct.name LIKE CONCAT('%',#{commodityInfo.typeName},'%')
AND coi.title LIKE CONCAT('%',#{commodityInfo.title},'%')
AND coi.status = #{commodityInfo.status}
order by coi.create_time desc
